package org.example;

import com.google.common.primitives.Longs;

import java.util.Base64;

import static org.junit.Assert.assertEquals;

public class Base64Coder {
    public static void main(String[] args) {
        String s = Long.toString(System.currentTimeMillis());
        byte[] encodeBody = Base64.getEncoder().encode(s.getBytes());
        byte[] decodeBody = Base64.getDecoder().decode(encodeBody);
        assertEquals(s, new String(decodeBody));

        System.out.println(Longs.fromByteArray(Base64.getDecoder().decode("AAAAAAAAAAE=")));
        System.out.println(Longs.fromByteArray(Base64.getDecoder().decode("AAAAACCR+RM=")));

        System.out.println(new String(Base64.getDecoder().decode("VXNlckRhdGFNb3ZlSW4=")));


        Base64.getDecoder().decode("");
    }
}
